package yl.demo.pathHelper.xml.model;

public class Context {
	public static final String TAG_CONTEXT = "context";
	public static final String TAG_MAP = "map";
	public static final String TAG_REF_DATA = "ref_data";
	public static final String TAG_DB_DATA = "db_data";
	
	private int map;
	private String refData;
	private DBData dbData;
	
	public Context(int map, String refData, DBData dbData) {
		// TODO Auto-generated constructor stub
		this.map = map;
		this.refData = refData;
		this.dbData = dbData;
	}
	
	public Context() {
		// TODO Auto-generated constructor stub
	}

	public int getMap() {
		return map;
	}
	public void setMap(int map) {
		this.map = map;
	}
	public String getRefData() {
		return refData;
	}
	public void setRefData(String refData) {
		this.refData = refData;
	}
	public DBData getDbData() {
		return dbData;
	}
	public void setDbData(DBData dbData) {
		this.dbData = dbData;
	}
}
